import React, { useState, useEffect } from 'react';
import './PostManagement.css';

const PostManagement = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filterStatus, setFilterStatus] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadPosts();
  }, []);

  const loadPosts = async () => {
    try {
      // Mock data for now - will be replaced with real API call
      const mockPosts = [
        {
          id: 1,
          content: 'Hello world! First post on our social platform. Excited to be here!',
          author: 'alice',
          author_name: 'Alice Johnson',
          created_at: '2024-10-10T14:30:00Z',
          likes_count: 24,
          comments_count: 8,
          status: 'published',
          media_url: null
        },
        {
          id: 2,
          content: 'Beautiful sunset at the beach today. Nature never fails to amaze me! üåÖ',
          author: 'bob',
          author_name: 'Bob Smith',
          created_at: '2024-10-10T18:45:00Z',
          likes_count: 42,
          comments_count: 15,
          status: 'published',
          media_url: 'https://example.com/sunset.jpg'
        },
        {
          id: 3,
          content: 'Working on a new project using React and Node.js. The learning never stops!',
          author: 'alice',
          author_name: 'Alice Johnson',
          created_at: '2024-10-11T09:20:00Z',
          likes_count: 18,
          comments_count: 6,
          status: 'published',
          media_url: null
        },
        {
          id: 4,
          content: 'Just captured this amazing wildlife photo. Check it out!',
          author: 'carol',
          author_name: 'Carol White',
          created_at: '2024-10-11T16:30:00Z',
          likes_count: 67,
          comments_count: 23,
          status: 'published',
          media_url: 'https://example.com/wildlife.jpg'
        },
        {
          id: 5,
          content: 'Draft post about machine learning trends...',
          author: 'alice',
          author_name: 'Alice Johnson',
          created_at: '2024-10-12T10:15:00Z',
          likes_count: 0,
          comments_count: 0,
          status: 'draft',
          media_url: null
        }
      ];
      setPosts(mockPosts);
    } catch (error) {
      console.error('Error loading posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredPosts = posts.filter(post => {
    const matchesSearch = post.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         post.author.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus === 'all' || post.status === filterStatus;
    return matchesSearch && matchesFilter;
  });

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      published: 'status-published',
      draft: 'status-draft',
      archived: 'status-archived'
    };
    return <span className={`status-badge ${statusClasses[status]}`}>{status}</span>;
  };

  if (loading) {
    return (
      <div className="post-management">
        <div className="loading">Loading posts...</div>
      </div>
    );
  }

  return (
    <div className="post-management">
      <div className="page-header">
        <h2>Post Management</h2>
        <p>Manage and monitor all posts on the platform</p>
        <button className="btn btn-primary">Create New Post</button>
      </div>

      <div className="post-controls">
        <div className="search-box">
          <input
            type="text"
            placeholder="Search posts or authors..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
        <div className="filter-controls">
          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="filter-select"
          >
            <option value="all">All Posts</option>
            <option value="published">Published</option>
            <option value="draft">Drafts</option>
            <option value="archived">Archived</option>
          </select>
        </div>
      </div>

      <div className="posts-list">
        {filteredPosts.map(post => (
          <div key={post.id} className="post-card">
            <div className="post-header">
              <div className="author-info">
                <div className="author-avatar">
                  {post.author.charAt(0).toUpperCase()}
                </div>
                <div>
                  <h4>@{post.author}</h4>
                  <p>{post.author_name}</p>
                </div>
              </div>
              <div className="post-meta">
                {getStatusBadge(post.status)}
                <span className="post-date">{formatDate(post.created_at)}</span>
              </div>
            </div>

            <div className="post-content">
              <p>{post.content}</p>
              {post.media_url && (
                <div className="media-indicator">
                  üì∑ Media attached
                </div>
              )}
            </div>

            <div className="post-stats">
              <div className="stat">
                <span className="stat-icon">‚ù§Ô∏è</span>
                <span>{post.likes_count} likes</span>
              </div>
              <div className="stat">
                <span className="stat-icon">üí¨</span>
                <span>{post.comments_count} comments</span>
              </div>
            </div>

            <div className="post-actions">
              <button className="btn btn-sm btn-secondary">Edit</button>
              <button className="btn btn-sm btn-secondary">View</button>
              <button className="btn btn-sm btn-danger">Delete</button>
            </div>
          </div>
        ))}
      </div>

      {filteredPosts.length === 0 && (
        <div className="empty-state">
          <h3>No posts found</h3>
          <p>Try adjusting your search or filter criteria</p>
        </div>
      )}
    </div>
  );
};

export default PostManagement;
